svg.script:
 
    
    var basePointX    = 250.;
    var basePointY    = 350.;
    var fixedPointX   = 10.;
    var fixedPointY   = 100.;
    var currentX      = 0.;
    var currentY      = 0.;
    var offsetX       = 0.;
    var offsetY       = 0.;
    var slantX        = 0.;
    var slantY        = 0.;
    var slantAngle    = 20.;
    var slantLength   = 100.;
    var radius        = 0.;
    var minorAxis     = 10.;
    var majorAxis     = 20.;
    var Constant      = 20000.;
    var hGap          = 2.;
    var angle         = 0.;
    var maxAngle      = 360.;
    var angleDelta    = 1.;
    var shortPause    = 10.;
    var strokeWidth   = 2.;
    var style         = "";
    var points        = "";
    var arcRadius     = majorAxis;
    var outerRadius   = 3*majorAxis/2;
    var innerRadius   = 1*outerRadius/3;
    var stripCount    = 20;
    var stripWidth    = Math.floor(maxAngle/stripCount);
    var frequency     = 5.; 
    var rVal          = 0.;
    var gVal          = 0.;
    var bVal          = 0.;
 
    var arcPathNode   = null;
    var ellipseNode   = null;
    var circleNode    = null;
    var theTimeout    = null;
    var svgDocument   = null;
    var target        = null;
    var gcNode        = null;

    var svgNS         = "http://www.w3.org/2000/svg";

    function init(event)
    {
       slantX = slantLength*Math.cos(
                            slantAngle*Math.PI/180);

       slantY = slantLength*Math.sin(
                            slantAngle*Math.PI/180);

       svgDocument = event.target.ownerDocument;
       gcNode = svgDocument.getElementById("gc");

       drawSpiral(event);
    }

    function drawSpiral(event) 
    {
       for(angle=1; angle<maxAngle; angle+=angleDelta)
       {
          radius = Constant*
                    Math.sin(angle*Math.PI/180)/angle;
  
          offsetX = radius*Math.cos(angle*Math.PI/180);
          offsetY = radius*Math.sin(angle*Math.PI/180);

          currentX = basePointX+offsetX;
          currentY = basePointY-offsetY;
  
          ellipseNode = 
                   svgDocument.createElementNS(svgNS, "ellipse");
   
          rVal = angle*255/maxAngle;
          gVal = 0.;
          bVal = 0.;

          fillColor = 
                  "rgb("+rVal+","+gVal+","+bVal+")";

          strokeColor = 
                  "rgb("+rVal+","+gVal+","+bVal+")";

        //style  = "fill:"+strokeColor;
        //style += ";stroke:yellow";
        //style += ";stroke-width:"+strokeWidth;

        //ellipseNode.setAttribute("style",style);
          ellipseNode.setAttribute("fill", strokeColor);
          ellipseNode.setAttribute("stroke", "yellow");
          ellipseNode.setAttribute("stroke-width", strokeWidth);

          ellipseNode.setAttribute("cx", currentX);
          ellipseNode.setAttribute("cy", currentY);
          ellipseNode.setAttribute("rx", majorAxis);
          ellipseNode.setAttribute("ry", minorAxis);
  
          if( angle % hGap == 0 )
          {
             addLine(currentX, currentY);
          } 

          gcNode.appendChild(ellipseNode);

          // attach a lateral circle...
          offsetX = outerRadius*Math.cos(
                            frequency*angle*Math.PI/180);

          offsetY = outerRadius*Math.sin(
                            frequency*angle*Math.PI/180);

          currentX += offsetX;
          currentY -= offsetY;

          rVal = (angle%stripWidth)*255/stripWidth;
          gVal = 0;
          bVal = 0;
    
          strokeColor = 
                  "rgb("+rVal+","+gVal+","+bVal+")";

        //style  = "stroke:white";
        //style += ";fill:rgb("+rVal+","+gVal+","+bVal+")";
   
          points  = "M"+currentX+","+currentY+" ";
          points += "Q"+(currentX+20)+","+currentY+" ";
          points += (currentX+40)+","+(currentY+40)+" ";
          points += "T"+(currentX+60)+","+(currentY+40)+" ";
          points += "M"+(currentX+10)+","+(currentY+10)+" ";
          points += "Q"+(currentX+40)+","+(currentY)+" ";
          points += (currentX+40)+","+(currentY+40)+" ";
          points += "T"+(fixedPointX+60)+","+(fixedPointY+40)+" z";

          arcPathNode = document.createElementNS(svgNS, "path");
           
        //arcPathNode.setAttribute("style",style);
          arcPathNode.setAttribute("fill", strokeColor);
          arcPathNode.setAttribute("stroke", "white");
        //arcPathNode.setAttribute("stroke-width", strokeWidth);

          arcPathNode.setAttribute("d", points);
          gcNode.appendChild(arcPathNode);
       }

    } // drawSpiral

    function addLine(currentX, currentY)
    {
       strokeColor = 
               "rgb("+rVal+","+gVal+","+bVal+")";

     //style  = "stroke:"+strokeColor+";";
     //style += "stroke-width:"+strokeWidth;

       lineNode = svgDocument.createElementNS(svgNS, "line");

     //lineNode.setAttribute("style",style);
     //lineNode.setAttribute("fill", strokeColor);
       lineNode.setAttribute("stroke", strokeColor);
       lineNode.setAttribute("stroke-width", strokeWidth);

       lineNode.setAttribute("x1", currentX);
       lineNode.setAttribute("y1", currentY);
       lineNode.setAttribute("x2", 
                             currentX-slantX);
       lineNode.setAttribute("y2", 
                             currentY-slantY);
   
       gcNode.appendChild(lineNode);

    } // addLine
