svg.script:
 
    
    var basePointX    = 300.;
    var basePointY    = 300.;
    var wavePointX    = 0;
    var wavePointY    = 100;
    var waveX         = 0.;
    var waveY         = 0.;
    var waveFactor    = 1.;
    var fixedPointX   = 0.;
    var fixedPointY   = 0.;
    var currentX      = 0.;
    var currentY      = 0.;
    var offsetX       = 0.;
    var offsetY       = 0.;
    var radius        = 0.;
    var minorAxis     = 10.;
    var majorAxis     = 20.;
    var Constant      = 20000.;
    var randomAngle   = 0.;
    var angle         = 0.;
    var maxAngle      = 360.;
    var angleDelta    = 2.;
    var shortPause    = 10.;
    var strokeWidth   = 3.;
    var amplitude     = 50.;
    var frequency     = 0.5;
    var strokeColor   = "red";
    var style         = "";
  //var dashStyle     = "4 4 4 4";
  //var dashStyle     = "8 8 8 8";
  //var dashStyle     = "12 12 12 12";
    var dashStyle     = "16 16 16 16"; 
    var dashStyle     = "4 4 4 4";
    var stripCount    = 8.;
    var stripWidth    = maxAngle/stripCount;
    var rVal          = 0.;
    var gVal          = 0.;
    var bVal          = 0.;
 
    var lineNode      = null;
    var ellipseNode   = null;
    var theTimeout    = null;
    var svgDocument   = null;
    var target        = null;
    var gcNode        = null;

    var svgNS         = "http://www.w3.org/2000/svg";

    function init(event)
    {
       svgDocument = event.target.ownerDocument;
       gcNode = svgDocument.getElementById("gc");

       drawSpiral(event);
    }

    function drawSpiral(event) 
    {
       randomAngle = maxAngle*Math.random();
       radius      = Constant*Math.sin(angle*Math.PI/180);

       offsetX     = radius*Math.cos(angle*Math.PI/180);
       offsetY     = radius*Math.sin(angle*Math.PI/180);

       fixedPointX = basePointX+offsetX;
       fixedPointY = basePointY-offsetY;

       for(angle=0; angle<maxAngle; angle+=angleDelta)
       {
          radius = Constant*
                    Math.sin(angle*Math.PI/180)/angle;
  
          offsetX = radius*Math.cos(angle*Math.PI/180);
          offsetY = radius*Math.sin(angle*Math.PI/180);

          currentX = basePointX+offsetX;
          currentY = basePointY-offsetY;
  
          ellipseNode = 
                   svgDocument.createElementNS(svgNS, "ellipse");
   
          rVal = (angle%stripWidth)*255/stripWidth;
          gVal = 0.;
          bVal = 0.;

          fillColor = 
                  "rgb("+rVal+","+gVal+","+bVal+")";

          strokeColor = 
                  "rgb("+rVal+","+gVal+","+bVal+")";

        //style  = "fill:"+strokeColor;
        //style += ";stroke:yellow";
        //style += ";stroke-width:"+strokeWidth;
        //style += ";stroke-dasharray:"+dashStyle;

        //ellipseNode.setAttribute("style",style);
          ellipseNode.setAttribute("fill", strokeColor);
          ellipseNode.setAttribute("stroke", "yellow");
        //ellipseNode.setAttribute("stroke-width", strokeWidth);
          ellipseNode.setAttribute("stroke-width", 1);
          ellipseNode.setAttribute("stroke-dasharray", dashStyle);

          ellipseNode.setAttribute("cx", currentX);
          ellipseNode.setAttribute("cy", currentY);
          ellipseNode.setAttribute("rx", majorAxis);
          ellipseNode.setAttribute("ry", minorAxis);
   
          gcNode.appendChild(ellipseNode);

          lineNode = svgDocument.createElementNS(svgNS, "line");
 
          strokeColor = 
                  "rgb("+rVal+","+gVal+","+bVal+")";

        //style  = "fill:"+strokeColor;
        //style += ";stroke:"+strokeColor;
        //style += ";stroke-width:"+strokeWidth;

        //lineNode.setAttribute("style",style);
          lineNode.setAttribute("fill", strokeColor);
          lineNode.setAttribute("stroke", strokeColor);
          lineNode.setAttribute("stroke-width", strokeWidth);

          lineNode.setAttribute("x1", currentX);
          lineNode.setAttribute("y1", currentY);
          lineNode.setAttribute("x2", fixedPointX);
          lineNode.setAttribute("y2", fixedPointY);
   
          gcNode.appendChild(lineNode);

          lineNode = svgDocument.createElementNS(svgNS, "line");
 
          offsetX = amplitude*Math.cos(
                          frequency*angle*Math.PI/180);

          offsetY = amplitude*Math.sin(
                          frequency*angle*Math.PI/180);

          waveX = wavePointX+angle/waveFactor+offsetX;
          waveY = wavePointY-offsetY;

        //style += ";stroke:"+strokeColor;
        //style += ";stroke-width:"+strokeWidth;

        //lineNode.setAttribute("style",style);
          lineNode.setAttribute("stroke", strokeColor);
          lineNode.setAttribute("stroke-width", strokeWidth);

          lineNode.setAttribute("x1", waveX);
          lineNode.setAttribute("y1", waveY);
          lineNode.setAttribute("x2", currentX);
          lineNode.setAttribute("y2", currentY);

          gcNode.appendChild(lineNode);
       }

    } // drawSpiral
