svg.script:
 
    
    var basePointX    = 450.;
    var basePointY    = 350.;
    var currentX      = 0.;
    var currentY      = 0.;
    var offsetX       = 0.;
    var offsetY       = 0.;
    var radius        = 0.;
    var minorAxis     = 15.;
    var majorAxis     = 30.;
    var spiralCount   = 4.;
    var Constant      = 300.;
    var angle         = 0.;
    var maxAngle      = 720.;
    var angleDelta    = 2.;
    var rGap          = 20.;
    var rWidth        = 2*majorAxis;
    var rHeight       = 2*minorAxis;
    var widthDelta    = 4.;
    var heightDelta   = 2.;
    var shortPause    = 10.;
    var strokeWidth   = 1.;
    var xVertex       = 0.;
    var yVertex       = 0.;
    var slantX        = 20.;
    var slantY        = 10.;
    var rectCount     = 0.;
    var points        = "";
    var strokeColor   = "";
    var style         = "";
    var rVal          = 0.;
    var gVal          = 0.;
    var bVal          = 0.;
    var sineCubed     = 0.;
    var cosineCubed   = 0.;

    var rectNode      = null;
    var polygonNode   = null;
    var textData      = null;
    var svgText       = null; 
    var theTimeout    = null;
    var svgDocument   = null;
    var target        = null;
    var gcNode        = null;
    var gcNode2       = null;

    var svgNS         = "http://www.w3.org/2000/svg";

    function init(event)
    {
       svgDocument = event.target.ownerDocument;
       gcNode = svgDocument.getElementById("gc");
       gcNode2 = svgDocument.getElementById("gc2");

       drawSpiral(event);
    }

    function drawSpiral(event) 
    {
       for(angle=0; angle<maxAngle; angle+=angleDelta)
       {
          sineCubed = Math.sin(angle*Math.PI/180)*
                      Math.sin(angle*Math.PI/180)*
                      Math.sin(angle*Math.PI/180);

          cosineCubed = Math.cos(angle*Math.PI/180)*
                        Math.cos(angle*Math.PI/180)*
                        Math.cos(angle*Math.PI/180);

          radius = Constant*(sineCubed-cosineCubed);
  
          offsetX = radius*Math.cos(angle*Math.PI/180);
          offsetY = radius*Math.sin(angle*Math.PI/180);

          currentX = basePointX+offsetX;
          currentY = basePointY-offsetY;
  
          rVal = angle*255/maxAngle;
          gVal = 0.;
          bVal = 0.;

          createText();

          if( angle % rGap == 0 )
          {
             addRectangleNode();
             rWidth  += widthDelta;
             rHeight += heightDelta;
          }
       }

    } // drawSpiral

    function createText()
    {
       strokeColor = "rgb("+rVal+","+gVal+","+bVal+")";

     //style  = "fill:"+strokeColor;
     //style += ";stroke:yellow";
     //style += ";stroke-width:"+strokeWidth;
     //style += ";font-size:48;";

       textData = svgDocument.createTextNode("SVG");
       svgText  = svgDocument.createElementNS(svgNS, "text");

     //svgText.setAttribute("style", style);
       svgText.setAttribute("fill", strokeColor);
       svgText.setAttribute("stroke", "yellow");
       svgText.setAttribute("stroke-width", strokeWidth);
       svgText.setAttribute("font-size", 48);

       svgText.setAttribute("x", currentX);
       svgText.setAttribute("y", currentY);
       svgText.appendChild(textData);

       gcNode.appendChild(svgText);

       style += ";font-size:48;";
       textData = svgDocument.createTextNode("SVG");
       svgText  = svgDocument.createElementNS(svgNS, "text");

     //svgText.setAttribute("style", style);
       svgText.setAttribute("fill", strokeColor);
       svgText.setAttribute("stroke", "yellow");
       svgText.setAttribute("stroke-width", strokeWidth);
       svgText.setAttribute("font-size", 48);

       svgText.setAttribute("x", currentX);
       svgText.setAttribute("y", currentY);
       svgText.appendChild(textData);
       gcNode2.appendChild(svgText);
    }

    function addRectangleNode(event) 
    {
       rectNode = svgDocument.createElementNS(svgNS, "rect");
   
     //style  = "fill:yellow;stroke:red;";
     //style  = "fill:url(#gradientDefinition2);";
     //style += "stroke-width:"+strokeWidth;

     //rectNode.setAttribute("style",style);
       rectNode.setAttribute("fill", "url(#gradientDefinition2)");
       rectNode.setAttribute("stroke", "red");
       rectNode.setAttribute("stroke-width", strokeWidth);

       rectNode.setAttribute("x",      
                             currentX-rWidth/2);

       rectNode.setAttribute("y",      
                             currentY-rHeight/2);

       rectNode.setAttribute("width",  rWidth);
       rectNode.setAttribute("height", rHeight);

       gcNode.appendChild(rectNode);

       // top horizontal strip... 
       polygonNode = svgDocument.createElementNS(svgNS, "polygon");
 
       xVertex = currentX-rWidth/2;
       yVertex = currentY-rHeight/2;
       points  = xVertex+" "+yVertex+" ";

       xVertex = currentX-rWidth/2+rWidth;
       yVertex = currentY-rHeight/2;
       points  += xVertex+" "+yVertex+" ";

       xVertex  = currentX-rWidth/2+rWidth+slantX;
       yVertex  = currentY-rHeight/2-slantY;
       points  += xVertex+" "+yVertex+" ";

       xVertex  = currentX-rWidth/2+slantX;
       yVertex  = currentY-rHeight/2-slantY;
       points  += xVertex+" "+yVertex;

     //style  = "fill:black;stroke:red;";
     //style += "stroke-width:"+strokeWidth;

     //polygonNode.setAttribute("style",style);
       polygonNode.setAttribute("fill", "black");
       polygonNode.setAttribute("stroke", "red");
       polygonNode.setAttribute("stroke-width", strokeWidth);

       polygonNode.setAttribute("points",points);
       gcNode.appendChild(polygonNode);

       // right vertical strip... 
       polygonNode = svgDocument.createElementNS(svgNS, "polygon");

       xVertex = currentX-rWidth/2+rWidth;
       yVertex = currentY-rHeight/2;
       points  = xVertex+" "+yVertex+" ";

       xVertex  = currentX-rWidth/2+rWidth+slantX;
       yVertex  = currentY-rHeight/2-slantY;
       points  += xVertex+" "+yVertex+" ";

       xVertex  = currentX-rWidth/2+rWidth+slantX;
       yVertex  = currentY-rHeight/2-slantY+rHeight;
       points  += xVertex+" "+yVertex+" ";

       xVertex  = currentX-rWidth/2+rWidth;
       yVertex  = currentY-rHeight/2+rHeight;
       points  += xVertex+" "+yVertex+" ";

     //polygonNode.setAttribute("style",style);

       if( rectCount % 2 == 0 ) 
       {
          polygonNode.setAttribute("fill", "yellow");
          polygonNode.setAttribute("stroke", "red");
          polygonNode.setAttribute("stroke-width", strokeWidth);
       }
       else
       {
          polygonNode.setAttribute("fill", "blue");
          polygonNode.setAttribute("stroke", "red");
          polygonNode.setAttribute("stroke-width", strokeWidth);
       }

       ++rectCount;

       polygonNode.setAttribute("points",points);
       gcNode.appendChild(polygonNode);
    }
